    private void addBtnMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_addBtnMouseClicked
        String straatnaam = straatnaamField.getText();
        String huisNr = huisNrField.getText();
        String plaats = plaatsField.getText();
        String postcode = postcodeField.getText();
        int maxSpelers = Integer.parseInt(maxSpelersField.getText());
        Locatie locatie = new Locatie(straatnaam, huisNr, plaats, postcode, maxSpelers);
        parent.addLocatie(locatie);
        this.setVisible(false);
        this.dispose();
        
    }//GEN-LAST:event_addBtnMouseClicked

    private void cancelBtnMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_cancelBtnMouseClicked
        this.setVisible(false);
        this.dispose();
    }//GEN-LAST:event_cancelBtnMouseClicked

